    case 'WEB_AUTHN':
    case 'SELECT_CHALLENGE':
      if (challengeResponse === 'PASSWORD_SRP' || challengeResponse === 'PASSWORD') {
        return {
          ChallengeName: challengeResponse,
          Session: session,
          $metadata: {}
        };
      }
      return initiateSelectedChallenge({
        username,
        session,
        selectedChallenge: challengeResponse,
        config,
        clientMetadata
